package com.hansung.android.kiwi;import android.content.Context;import android.view.Gravity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.FrameLayout;import android.widget.LinearLayout;import android.widget.TextView;public class BalloonOverlayView extends FrameLayout {	private LinearLayout layout;	private TextView title, subTitle;	private TextView go_batt;		public BalloonOverlayView(Context context, String T, String ST) {		super(context);		setPadding(10, 0, 10, 0);		layout = new LinearLayout(context);		layout.setVisibility(VISIBLE);		setupView(context, layout, T, ST);				LayoutParams params = new LayoutParams(								LayoutParams.WRAP_CONTENT, LayoutParams.WRAP_CONTENT);		params.gravity = Gravity.NO_GRAVITY;		addView(layout, params);	}			protected void setupView(Context context, final ViewGroup parent, String T, String ST) {		LayoutInflater inflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);						View view = inflater.inflate(R.layout.bubble_popup, parent, true);		title = (TextView) view.findViewById(R.id.bubble_title);		subTitle = (TextView) view.findViewById(R.id.bubble_subtitle1);		setTitle(T);		setSubTitle(ST);		go_batt = view.findViewById(R.id.go_batt);	}	public void setTitle(String str) {		title.setText(str);	}	public void setSubTitle(String str) {		subTitle.setText(str);	}	public String getTitle() {		String s = (String) title.getText();		return s;	}}